mapper.insert:
insert into SC_COMMENT (ID, TYPE, CONTENT, 
  CREATE_TIME, SCORE, ISHIDDEN, 
  PARENT_ID, CREATE_USER_ID, FLAG
  )
values (#{id,jdbcType=VARCHAR}, #{type,jdbcType=CHAR}, #{content,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{score,jdbcType=DECIMAL}, #{ishidden,jdbcType=CHAR}, 
  #{parentId,jdbcType=VARCHAR}, #{createUserId,jdbcType=VARCHAR}, #{flag,jdbcType=CHAR}
  )

mapper.select:
    select 
    
    from sc_comment a,sc_comments_relation b 
	where a.id=b.comments_id
    and a.flag=1 and b.flag=a.flag
	
		and b.ref_id=#{map.contactid,jdbcType=VARCHAR}
	
	
		and a.COMMENT_PEOPLE=#{map.userid,jdbcType=VARCHAR}
	
	
		and b.type=#{map.type,jdbcType=VARCHAR}
