mapper.select:
    select 
    
    from (
	select * from SC_EVENTS where isuse=1 and flag=1
	union
  	select * from SC_EVENTS where isuse is null and start_time is not null and end_time is not null and flag=1
	)
    where 1=1
    
    	and NAME=#{map.event_name,jdbcType=VARCHAR}
    
    
    	and ID=#{map.event_id,jdbcType=VARCHAR}
    
    
    	and END_TIME>=#{map.start_time,jdbcType=TIMESTAMP,typeHandler=cn.sqhl.shop.core.DateTypeHandler}
    
    
    	and START_TIME<=#{map.end_time,jdbcType=TIMESTAMP,typeHandler=cn.sqhl.shop.core.DateTypeHandler}
