mapper.select:
    select 
    
    from SC_PURSE b
    where 1=1
	
    	and b.CREATE_TIME>=#{map.Btime,jdbcType=VARCHAR,typeHandler=cn.sqhl.shop.core.DateTypeHandler}
    
	
    	and b.CREATE_TIME<=#{map.Etime,jdbcType=VARCHAR,typeHandler=cn.sqhl.shop.core.DateTypeHandler}
    
	
    	and b.TRADE_AMOUNT>=#{map.Bamount,jdbcType=VARCHAR}
    
	
    	and b.TRADE_AMOUNT<=#{map.Eamount,jdbcType=VARCHAR}
    
	
    	and b.USER_ID=#{map.userid,jdbcType=VARCHAR}
    
	
    	and b.TRADE_TYPE=#{map.way,jdbcType=VARCHAR}
    
	
    	and b.OPTION_TYPE=#{map.type,jdbcType=VARCHAR}
    
	
    	and b.PURSE_TYPE=#{map.kind,jdbcType=VARCHAR}
    
	
    	and b.TRADE_STATE=#{map.status,jdbcType=VARCHAR}
